package com.consol.citrus.samples;

import com.consol.citrus.annotations.CitrusTest;
import com.consol.citrus.dsl.JUnit4CitrusTestDesigner;
import org.junit.Test;

/**
 * TODO: Description
 *
 * @author Unknown
 */
public class SampleTest extends JUnit4CitrusTestDesigner {

    @Test
    @CitrusTest
    public void EchoSampleTest() {
        variable("time", "citrus:currentDate()");
        echo("Hello Citrus!");
        echo("CurrentTime is: ${time}");
    }

    @Test
    @CitrusTest(name = "CitrusEchoTest")
    public void echoTest() {
        echo("Hello Citrus!");
    }
}
